import React, { useState, useEffect } from 'react';
import { getPedidos, excluirPedido, atualizarPedido } from '../services/api';
import { FaWhatsapp, FaEdit, FaTrash } from 'react-icons/fa';

// --- Tipos para o Pedido ---
interface Pedido {
  id: number;
  cliente_id: number;
  data_hora: string;
  pizza: string;
  ingredientes: string;
  valor: number;
  nome_cliente: string;
  telefone_cliente: string;
}

// Componente wrapper para ícones (Solução 3)
const IconWrapper: React.FC<{ children: React.ReactNode }> = ({ children }) => {
  return <>{children}</>;
};

const AdminPanel: React.FC = () => {
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);
  const [pedidos, setPedidos] = useState<Pedido[]>([]);
  const [editandoPedido, setEditandoPedido] = useState<Pedido | null>(null);
  const [mostrarModalEdicao, setMostrarModalEdicao] = useState(false);

  useEffect(() => {
    const fetchPedidos = async () => {
      try {
        const data = await getPedidos();
        setPedidos(data);
      } catch (err) {
        setError('Erro ao carregar pedidos: ' + (err instanceof Error ? err.message : String(err)));
      } finally {
        setLoading(false);
      }
    };

    fetchPedidos();
  }, []);

  // --- Função para lidar com o clique no botão de WhatsApp ---
  const handleCliqueWhatsApp = (numeroTelefone: string, nomeCliente: string, idPedido: number) => {
    const numeroLimpo = numeroTelefone.replace(/\D/g, '');
    let numeroFormatado = numeroLimpo;

    if (numeroLimpo.length === 10) {
      numeroFormatado = '55' + numeroLimpo.substring(0, 2) + '9' + numeroLimpo.substring(2);
    } else if (numeroLimpo.length === 11) {
      numeroFormatado = '55' + numeroLimpo;
    } else {
      console.error("Número de telefone inválido ou incompleto para formato internacional.", numeroLimpo);
      alert("Número de telefone inválido para envio via WhatsApp.");
      return;
    }

    const mensagem = `Olá ${encodeURIComponent(nomeCliente)}, sou da Pizzaria IA. Estou entrando em contato sobre seu pedido #${idPedido}.`;
    const mensagemCodificada = encodeURIComponent(mensagem);
    const link = `https://wa.me/${numeroFormatado}?text=${mensagemCodificada}`;
    window.open(link, '_blank');
  };

  // --- Função para lidar com o clique no botão de Excluir ---
  const handleExcluirPedido = async (id: number) => {
    if (window.confirm("Tem certeza que deseja excluir este pedido?")) {
      try {
        await excluirPedido(id);
        setPedidos(prev => prev.filter(p => p.id !== id));
        alert("Pedido excluído com sucesso!");
      } catch (err) {
        setError('Erro ao excluir pedido: ' + (err instanceof Error ? err.message : String(err)));
        alert("Erro ao excluir pedido. Consulte o console.");
      }
    }
  };

  // --- Função para lidar com o clique no botão de Editar ---
  const handleEditarPedido = (pedido: Pedido) => {
    setEditandoPedido(pedido);
    setMostrarModalEdicao(true);
  };

  // --- Função para lidar com o envio do formulário de edição ---
  const handleSubmitEdicao = async (e: React.FormEvent) => {
    e.preventDefault();
    if (!editandoPedido) return;

    try {
      await atualizarPedido(editandoPedido.id, editandoPedido);
      setPedidos(prev => prev.map(p => p.id === editandoPedido.id ? editandoPedido : p));
      setMostrarModalEdicao(false);
      setEditandoPedido(null);
      alert("Pedido atualizado com sucesso!");
    } catch (err) {
      setError('Erro ao atualizar pedido: ' + (err instanceof Error ? err.message : String(err)));
      alert("Erro ao atualizar pedido. Consulte o console.");
    }
  };

  // --- Função para lidar com mudanças no formulário de edição ---
  const handleChangeEdicao = (e: React.ChangeEvent<HTMLInputElement | HTMLTextAreaElement>) => {
    if (!editandoPedido) return;
    const { name, value } = e.target;
    setEditandoPedido(prev => ({ ...prev!, [name]: value }));
  };

  if (loading) return <div>Carregando pedidos...</div>;
  if (error) return <div style={{ color: 'red' }}>{error}</div>;

  return (
    <div style={{ padding: '20px', fontFamily: 'Arial, sans-serif' }}>
      <h2>Painel Administrativo - Pedidos</h2>
      {pedidos.length === 0 ? (
        <p>Nenhum pedido registrado ainda.</p>
      ) : (
        <table style={{ width: '100%', borderCollapse: 'collapse', marginTop: '10px' }}>
          <thead>
            <tr>
              <th>ID Pedido</th>
              <th>Nome Cliente</th>
              <th>Telefone</th>
              <th>Pizza</th>
              <th>Valor</th>
              <th>Data/Hora</th>
              <th>Ações</th>
            </tr>
          </thead>
          <tbody>
            {pedidos.map((pedido) => (
              <tr key={pedido.id}>
                <td>{pedido.id}</td>
                <td>{pedido.nome_cliente}</td>
                <td>{pedido.telefone_cliente}</td>
                <td>{pedido.pizza}</td>
                <td>R$ {pedido.valor.toFixed(2)}</td>
                <td>{new Date(pedido.data_hora).toLocaleString()}</td>
                <td>
                  {/* Solução com IconWrapper */}
                  <button
                    onClick={() => handleCliqueWhatsApp(pedido.telefone_cliente, pedido.nome_cliente, pedido.id)}
                    style={{
                      padding: '6px 8px',
                      marginRight: '4px',
                      backgroundColor: '#25D366',
                      color: 'white',
                      border: 'none',
                      borderRadius: '4px',
                      cursor: 'pointer',
                      display: 'flex',
                      alignItems: 'center',
                      justifyContent: 'center',
                      fontSize: '14px',
                    }}
                    title={`Enviar mensagem para ${pedido.nome_cliente}`}
                  >
                    <IconWrapper>
                      <FaWhatsapp size={16} />
                    </IconWrapper>
                  </button>

                  <button
                    onClick={() => handleEditarPedido(pedido)}
                    style={{
                      padding: '6px 8px',
                      marginRight: '4px',
                      backgroundColor: '#ffc107',
                      color: 'black',
                      border: 'none',
                      borderRadius: '4px',
                      cursor: 'pointer',
                      display: 'flex',
                      alignItems: 'center',
                      justifyContent: 'center',
                      fontSize: '14px',
                    }}
                    title={`Editar pedido #${pedido.id}`}
                  >
                    <IconWrapper>
                      <FaEdit size={16} />
                    </IconWrapper>
                  </button>

                  <button
                    onClick={() => handleExcluirPedido(pedido.id)}
                    style={{
                      padding: '6px 8px',
                      backgroundColor: '#dc3545',
                      color: 'white',
                      border: 'none',
                      borderRadius: '4px',
                      cursor: 'pointer',
                      display: 'flex',
                      alignItems: 'center',
                      justifyContent: 'center',
                      fontSize: '14px',
                    }}
                    title={`Excluir pedido #${pedido.id}`}
                  >
                    <IconWrapper>
                      <FaTrash size={16} />
                    </IconWrapper>
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}

      {/* Modal de Edição */}
      {mostrarModalEdicao && editandoPedido && (
        <div style={styles.modalOverlay}>
          <div style={styles.modalContent}>
            <h3>Editando Pedido #{editandoPedido.id}</h3>
            <form onSubmit={handleSubmitEdicao}>
              <label>
                Nome Cliente:
                <input
                  type="text"
                  name="nome_cliente"
                  value={editandoPedido.nome_cliente}
                  onChange={handleChangeEdicao}
                  required
                  style={styles.input}
                />
              </label>
              <label>
                Telefone Cliente:
                <input
                  type="tel"
                  name="telefone_cliente"
                  value={editandoPedido.telefone_cliente}
                  onChange={handleChangeEdicao}
                  required
                  style={styles.input}
                />
              </label>
              <label>
                Pizza:
                <input
                  type="text"
                  name="pizza"
                  value={editandoPedido.pizza}
                  onChange={handleChangeEdicao}
                  required
                  style={styles.input}
                />
              </label>
              <label>
                Ingredientes:
                <textarea
                  name="ingredientes"
                  value={editandoPedido.ingredientes}
                  onChange={handleChangeEdicao}
                  required
                  style={{ ...styles.input, minHeight: '60px' }}
                />
              </label>
              <label>
                Valor (R$):
                <input
                  type="number"
                  name="valor"
                  value={editandoPedido.valor}
                  onChange={handleChangeEdicao}
                  required
                  min="0"
                  step="0.01"
                  style={styles.input}
                />
              </label>
              <div style={styles.modalButtons}>
                <button type="button" onClick={() => setMostrarModalEdicao(false)} style={styles.cancelButton}>
                  Cancelar
                </button>
                <button type="submit" style={styles.saveButton}>
                  Salvar
                </button>
              </div>
            </form>
          </div>
        </div>
      )}
    </div>
  );
};

// --- Estilos CSS em JS ---
const styles = {
  modalOverlay: {
    position: 'fixed' as const,
    top: 0,
    left: 0,
    right: 0,
    bottom: 0,
    backgroundColor: 'rgba(0,0,0,0.5)',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
    zIndex: 1000,
  },
  modalContent: {
    backgroundColor: 'white',
    padding: '20px',
    borderRadius: '8px',
    width: '90%',
    maxWidth: '500px',
    maxHeight: '80vh',
    overflowY: 'auto' as const,
  },
  input: {
    width: '100%',
    padding: '8px',
    margin: '4px 0 12px 0',
    border: '1px solid #ccc',
    borderRadius: '4px',
    boxSizing: 'border-box' as const,
  },
  modalButtons: {
    display: 'flex',
    justifyContent: 'space-between',
    marginTop: '16px',
  },
  cancelButton: {
    padding: '8px 16px',
    backgroundColor: '#e0e0e0',
    color: 'black',
    border: 'none',
    borderRadius: '4px',
    cursor: 'pointer',
  },
  saveButton: {
    padding: '8px 16px',
    backgroundColor: '#007bff',
    color: 'white',
    border: 'none',
    borderRadius: '4px',
    cursor: 'pointer',
  },
};

export default AdminPanel;
