import React, { useState, useEffect } from 'react';
import { getPedidos, excluirPedido, atualizarPedido } from '../services/api';
import { FaWhatsapp, FaEdit, FaTrash, FaSpinner, FaExclamationTriangle, FaPizzaSlice, FaPrint } from 'react-icons/fa';

// --- Tipos para o Pedido ---
interface Pedido {
  id: number;
  cliente_id: number;
  data_hora: string;
  pizza: string;
  ingredientes: string;
  valor: number;
  nome_cliente: string;
  telefone_cliente: string;
}

// Componente wrapper para √≠cones
const IconWrapper: React.FC<{ children: React.ReactNode }> = ({ children }) => {
  return <>{children}</>;
};

const AdminPanel: React.FC = () => {
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);
  const [pedidos, setPedidos] = useState<Pedido[]>([]);
  const [editandoPedido, setEditandoPedido] = useState<Pedido | null>(null);
  const [mostrarModalEdicao, setMostrarModalEdicao] = useState(false);
  const [processando, setProcessando] = useState(false);

  useEffect(() => {
    const fetchPedidos = async () => {
      try {
        const data = await getPedidos();
        setPedidos(data);
      } catch (err) {
        setError('Erro ao carregar pedidos: ' + (err instanceof Error ? err.message : String(err)));
      } finally {
        setLoading(false);
      }
    };

    fetchPedidos();
  }, []);

  // --- Fun√ß√£o para imprimir comanda ---
  const handleImprimirComanda = (pedido: Pedido) => {
    // Criar uma nova janela para impress√£o
    const janelaImpressao = window.open('', '_blank');
    if (!janelaImpressao) {
      alert('Por favor, permita pop-ups para imprimir a comanda.');
      return;
    }

    // Conte√∫do HTML para a comanda
    const conteudoComanda = `
      <!DOCTYPE html>
      <html>
      <head>
        <title>Comanda - Pedido #${pedido.id}</title>
        <style>
          @media print {
            @page {
              margin: 0.5cm;
              size: 80mm 150mm;
            }
            body {
              margin: 0;
              padding: 0;
              font-family: 'Courier New', monospace;
              font-size: 12px;
              line-height: 1.2;
              color: #000;
            }
          }
          body {
            margin: 0;
            padding: 10px;
            font-family: 'Courier New', monospace;
            font-size: 12px;
            line-height: 1.2;
