      {!isOpen ? (
        <button className="floating-button" onClick={() => setIsOpen(true)}>
          üçï
        </button>
      ) : (
        <div className="chat-modal">
          <div className="chat-header">
            <h3>Pizzaria IA</h3>
            <button onClick={() => setIsOpen(false)}>√ó</button>
          </div>
          <div className="chat-body">
